public class javaparameters {

	public static void main(String[] args) {
		
	displayName("ab",5);
	}
	public static void displayName(String name,int number)//parameters
	{
		for(int counter=1; counter<=number;counter++) {
			System.out.println(name);
		}
		
	}
	

}



